import mongoose from 'mongoose'
const Schema=mongoose.Schema


const UserSchema=new Schema({
name:{
    type:String,
    required:true,
},
email:{
type:String,
required: true,
},
password:{
  type: String,
   required: true
},
contact:{
    type:Number,
    default:null
},
status:{
    type:Number,
    default:1,
},
created_at:{
    type:Date,
    default:Date.now()
},

})


export default mongoose.model("user",UserSchema)

