import multer from "multer";
import fs from "fs";
import path from "path";
import mongoose from "mongoose";

import productModel from "../Models/product.model";
const storage = multer.diskStorage({
  destination: function (req, file, cb) {
    if (fs.existsSync("./uploads/product")) {
      cb(null, "./uploads/product");
    } else {
      fs.mkdirSync("./uploads/product");
      cb(null, "./uploads/product");
    }
  },
  filename: function (req, file, cb) {
    const orgName = file.originalname;
    const ext = path.parse(orgName).ext;
    const name = path.parse(orgName).name;
    const fullname =
      name + "-" + Date.now() + "" + Math.round(Math.random() * 1e9) + ext;

    cb(null, fullname);
  },
});
const upload = multer({ storage: storage });

export const Addproduct = async (req, res) => {
  try {
    const fileWithData = upload.fields([
      { name: "thumbnail", maxCount: 1 },
      { name: "images", maxCount: 10 },
    ]);
    fileWithData(req, res, async function (err) {
      if (err)
        return res.status(400).json({
          Message: err.Message,
          success: false,
        });
      let img = null;
      let imgArr = [];

      if (req.files) {
        if (req.files["thumbnail"][0]) {
          console.log(req.files["thumbnail"][0]);

          img = req.files["thumbnail"][0].filename;
        }

        if (req.files["images"]) {
          imgArr = req.files["images"].map((file) => file.filename);
        }
      }
      // console.log(req.files["thumbnail"]);
      // console.log(req.files["images"]);

      const { title, description, price, quantity, category, Tag } = req.body;
      const saveData = await productModel.create({
        title: title,
        description: description,
        price: price,
        quantity: quantity,
        category: category,
        Tag: Tag,
        thumbnail:imgArr[0],
        images: imgArr,
      });
      // saveData.save()
      if (saveData) {
        return res.status(200).json({
          success: true,
          data: saveData,
          message: "Product added successfully",
          filepath: "http://localhost:8001/uploads/product",
        });
      }
      return res.status(400).json({
        message: "Bad request",
        success: false,
      });
    });
  } catch (err) {
    return res.status(500).json({
      message: err.message,
      success: false,
    });
  }
};



export const getproduct = async (req, res) => {
  try {
    const productid = req.params.productid;
    if (!mongoose.Types.ObjectId.isValid(productid)) {
      return res.status(400).json({
        message: "Invalid Product ID",
        success: false,
      });
    }
    const product = await productModel.findOne({_id:productid})
    if (product) {
      return res.status(200).json({
        data: product,
        message: "fetched",
        success: true,
        filepath: "http://localhost:8001/uploads/product",
      });
    }
    return res.status(400).json({
      message: "No Product Found",
      success: false,
    });
  } catch (err) {
    return res.status(500).json({
      message: err.message,
      success: false,
    });
  }
};











// export const getproduct = async (req, res) => {
//   try {
//     const productid = req.params.productid;
//     const product = await productModel.findOne({ _id: productid });
//     if (product) {
//       return res.status(200).json({
//         data: product,
//         message: "fetched",
//         success: true,
//         filepath: "http://localhost:8001/uploads/product",
//       });
//     }
//     return res.status(400).json({
//       message: "No Product Found",
//       success: false,
//     });
//   } catch (err) {
//     return res.status(500).json({
//       message: err.message,
//       success: false,
//     });
//   }
// };


export const getproducts = async (req, res) => {
  try {


  

    const { page, limit, search, sort } = req.query;

    //ek page me kitna chahiye
    const skipno = limit * (page - 1);

    const rgx = (pattern) => {
      return new RegExp(`.*${pattern}*.`);
    };

    
    let filter = {};
    if (search) {
      const searchRgx = rgx(search);
      filter = {
        $or: [
          { title: { $regex: searchRgx, $options: "i" } },
          {"category.name":{$regex: searchRgx, $options: "i" }},
          { description: { $regex: searchRgx, $options: "i" } },
        ],
      };
    }

    //sorting
    let sortValue = ["_id", 1];
    if (sort == "new") {
      sortValue = ["_id", -1];
    }
    if (sort=="old"){
      sortValue = ["_id", 1];
    }
    if (sort == "lth") {
      sortValue = ["price", 1];
    }
    if (sort == "htl") {
      sortValue = ["price", -1];
    }

    const products = await productModel
      .find(filter)
      .populate("category")
      .populate("Tag")
      .limit(limit)
      .skip(skipno)
      .sort([sortValue]);

    if (products) {
      return res.status(200).json({
        count: products.length,
        data: products,
        message: "All Products Fetched Successfully",
        success: true,
        filepath: "http://localhost:8001/uploads/product",
      });
    }
    return res.status(400).json({
      message: "Bad request",
      success: false,
    });
  } catch (err) {
    return res.status(500).json({
      message: err.message,
      success: false,
    });
  }
};

