export const getproducts = async (req, res) => {
  try {
    const { page, limit, search, sort, min, max } = req.query;

    //ek page me kitna chahiye
    const skipno = limit * (page - 1);

    const rgx = (pattern) => {
      return new RegExp(`.*${pattern}*.`);
    };

    let filter = {};
    if (search) {
      const searchRgx = rgx(search);
      filter = {
        $or: [
          { title: { $regex: searchRgx, $options: "i" } },
          { "category.name": { $regex: searchRgx, $options: "i" } },
          { description: { $regex: searchRgx, $options: "i" } },
          { price: { $regex: searchRgx, $options: "i" } },
        ],
      };
    }

    //sorting
    let sortValue = ["_id", 1];
    if (sort == "new") {
      sortValue = ["_id", -1];
    }
    if (sort == "old") {
      sortValue = ["_id", 1];
    }
    if (sort == "lth") {
      sortValue = ["price", 1];
    }
    if (sort == "htl") {
      sortValue = ["price", -1];
    }
    if (min && max) {
      filter = { ...filter, price: { $gte: min, $lte: max } };
    }
const totalproducts=await productModel.countDocuments()
    const products = await productModel
      .find(filter)
      .populate("category")
      .populate("Tag")
      .limit(Number(limit))
      .skip(Number(skipno))
      .sort([sortValue]);

    if (products) {
      return res.status(200).json({
        count: products.length,
        totalproducts:totalproducts,
        data: products,
        message: "All Products Fetched Successfully",
        success: true,
        filepath: "http://localhost:8001/uploads/product",
      });
    }
    return res.status(400).json({
      message: "Bad request",
      success: false,
    });
  } catch (err) {
    return res.status(500).json({
      message: err.message,
      success: false,
    });
  }
};

