  title: {
    type: String,
    required: true,
  },
  category: {
    type: Schema.Types.ObjectId,
    require: true,
    ref: blogCategoryModel,
  },
  Tag: {
    required: true,
    type: Schema.Types.ObjectId,
    ref: BlogTagModel,
  },
  thumbnail: {
    type: String,
    default: null,
  },
  status: {
    type: Number,
    default: 1,
  },
  created_at: {
    type: Date,
    default: Date.now(),
  },
});

export default mongoose.model("Blog", blogSchema);

